import React, { useState, useEffect } from 'react';
import { 
  Table, 
  Card, 
  Row, 
  Col, 
  Select, 
  Input, 
  Space,
  Typography,
  Statistic
} from 'antd';
import { ReloadOutlined } from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { mockDiskData } from './mockData';

const { Title } = Typography;
const { Search } = Input;

interface DiskPrice {
  product_name: string;
  capacity: string;
  price: string;
  price_per_tb: string;
  interface: string;
  form_factor: string;
  seller: string;
  rating?: string;
  product_url?: string;
  seller_url?: string;
  date_scraped: string;
}

const App: React.FC = () => {
  const [data, setData] = useState<DiskPrice[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchText, setSearchText] = useState('');
  const [filterType, setFilterType] = useState('all');

  useEffect(() => {
    // 使用模拟数据进行测试
    setData(mockDiskData);
    setLoading(false);
    
    // 当后端准备好后，可以取消注释以下代码
    // fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('/api/latest');
      const json = await response.json();
      setData(json);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const columns: ColumnsType<DiskPrice> = [
    {
      title: '产品名称',
      dataIndex: 'product_name',
      render: (text, record) => (
        <a href={record.product_url} target="_blank" rel="noopener noreferrer">
          {text}
        </a>
      ),
      sorter: (a, b) => a.product_name.localeCompare(b.product_name),
    },
    {
      title: '容量',
      dataIndex: 'capacity',
      sorter: (a, b) => {
        const aNum = parseFloat(a.capacity);
        const bNum = parseFloat(b.capacity);
        return aNum - bNum;
      },
    },
    {
      title: '价格',
      dataIndex: 'price',
      sorter: (a, b) => {
        const aNum = parseFloat(a.price.replace('$', ''));
        const bNum = parseFloat(b.price.replace('$', ''));
        return aNum - bNum;
      },
    },
    {
      title: '每TB价格',
      dataIndex: 'price_per_tb',
      sorter: (a, b) => {
        const aNum = parseFloat(a.price_per_tb.replace('$', ''));
        const bNum = parseFloat(b.price_per_tb.replace('$', ''));
        return aNum - bNum;
      },
    },
    {
      title: '接口',
      dataIndex: 'interface',
      filters: [
        { text: 'SATA', value: 'SATA' },
        { text: 'NVMe', value: 'NVMe' },
        { text: 'USB', value: 'USB' },
      ],
      onFilter: (value, record) => record.interface.includes(value as string),
    },
    {
      title: '硬盘形态',
      dataIndex: 'form_factor',
    },
    {
      title: '卖家',
      dataIndex: 'seller',
      render: (text, record) => (
        <a href={record.seller_url} target="_blank" rel="noopener noreferrer">
          {text}
        </a>
      ),
    },
    {
      title: '评分',
      dataIndex: 'rating',
    },
  ];

  const filteredData = data.filter(item => {
    const matchesSearch = item.product_name.toLowerCase().includes(searchText.toLowerCase());
    const matchesType = filterType === 'all' || item.interface.includes(filterType);
    return matchesSearch && matchesType;
  });

  return (
    <div style={{ padding: '24px' }}>
      <Title level={2}>硬盘价格监控</Title>
      
      <Row gutter={[16, 16]}>
        <Col span={8}>
          <Card>
            <Statistic title="总产品数" value={data.length} />
          </Card>
        </Col>
        <Col span={8}>
          <Card>
            <Statistic title="数据更新时间" value={data[0]?.date_scraped || '-'} />
          </Card>
        </Col>
        <Col span={8}>
          <Card>
            <Statistic 
              title="价格范围" 
              value={data.length ? `$${Math.min(...data.map(item => parseFloat(item.price.replace('$', ''))))} - $${Math.max(...data.map(item => parseFloat(item.price.replace('$', ''))))}` : '-'} 
            />
          </Card>
        </Col>
      </Row>

      <Card style={{ marginTop: '24px' }}>
        <Space style={{ marginBottom: '16px' }}>
          <Search
            placeholder="搜索产品"
            onChange={e => setSearchText(e.target.value)}
            style={{ width: 200 }}
          />
          <Select
            defaultValue="all"
            style={{ width: 120 }}
            onChange={value => setFilterType(value)}
            options={[
              { value: 'all', label: '全部类型' },
              { value: 'SATA', label: 'SATA' },
              { value: 'NVMe', label: 'NVMe' },
              { value: 'USB', label: 'USB' },
            ]}
          />
          <ReloadOutlined onClick={fetchData} spin={loading} />
        </Space>

        <Table
          columns={columns}
          dataSource={filteredData}
          loading={loading}
          rowKey="product_name"
          pagination={{ pageSize: 10 }}
          scroll={{ x: true }}
        />
      </Card>
    </div>
  );
};

export default App;
